using Microsoft.EntityFrameworkCore;
using School.Models;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

namespace School.Data
{
    public class SchoolDbContext : DbContext
    {
        public SchoolDbContext(DbContextOptions<SchoolDbContext> options) : base(options)
        {

        }
        //Fluent API
        protected override void OnModelCreating(ModelBuilder modelBuilder)
        {
            modelBuilder.Entity<Student>().Property(r => r.Email).IsRequired();

            modelBuilder.Entity<Student>().HasData(new Student { StudentId = 1, Name = "Adel Kalu", Email = "Adel@Kalu", CourseId = 1 });
            modelBuilder.Entity<Profile>().HasData(new Profile { ProfileId = 1, Image = "data:image/jpeg;base64,", City = "Makkah", Country = "Saudi Arabia", StudentId = 1 });
            modelBuilder.Entity<Teacher>().HasData(new Teacher { TeacherId = 1, Name = "Abduallah Aid", Email = "Abduallah@Aid" });
            modelBuilder.Entity<Course>().HasData(new Course { CourseId = 1, CourseName = "ASP.NET", Description = "ASP.NET with React" });
            modelBuilder.Entity<StudentTeacher>().HasData(new StudentTeacher { StudentTeacherId = 1, StudentId = 1 , TeacherId = 1 });

        }
        public DbSet<Student> students { get; set; }
        public DbSet<Profile> profiles { get; set; }
        public DbSet<Course> courses { get; set; }
        public DbSet<Teacher> teachers { get; set; }
        public DbSet<StudentTeacher> studentTeachers { get; set; }


    }
}
